      &lt;td class="dt-nodeid"&gt;2&lt;/td&gt;&lt;td class="leadalt"&gt;*&lt;/td&gt;&lt;td class="lead-row1" colspan="3"&gt;&lt;table class="nested" width="100%" cellspacing="0" cellpadding="0"&gt;&lt;tr&gt;&lt;td class="leadtxt" width="95%"&gt;&lt;span class="leadspan"&gt;An jedem dritten Knoten fehlen Ranke und Blütenstand; Blätter behaart, aber Haare weder rostbraun noch spinnwebartig&nbsp;&lt;/span&gt;&lt;/td&gt;&lt;td&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td class="leadresult" colspan="2"&gt;&lt;span class="commonnames"&gt;&nbsp;Rostrote&nbsp;Rebe, Scharlachwein&nbsp;&nbsp;–&lt;/span&gt;&lt;span class="leadresults"&gt;&lt;span class="leadout nowrap"&gt;&nbsp;&nbsp;&lt;a href="http://offene-naturfuehrer.de/w/index.php?title=Vitis_coignetiae&amp;action=edit&amp;redlink=1" class="new" title="Vitis coignetiae (Seite nicht vorhanden)"&gt;&lt;i&gt;Vitis coignetiae&lt;/i&gt;&lt;/a&gt;&lt;/span&gt; &lt;span class="resultqualifier"&gt; &lt;span style="font-variant:small-caps"&gt;Pulliat&lt;/span&gt; ex &lt;span style="font-variant:small-caps"&gt;Planch.&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table class="nested"&gt;&lt;tr style="vertical-align:top;"&gt;&lt;td&gt;&lt;div style="padding:0em 0.5em 1em 0;float:left;width:615px;"&gt;&lt;div class="floatnone"&gt;&lt;a href="http://offene-naturfuehrer.de/web/Datei:Dryades_TSB78151.jpg" class="image"&gt;&lt;img alt="Dryades TSB78151.jpg" src="http://species-id.net/o/media/thumb/a/a9/Dryades_TSB78151.jpg/560px-Dryades_TSB78151.jpg" width="560" height="400" /&gt;&lt;/a&gt;&lt;/div&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/td&gt;&lt;td&gt;&lt;/td&gt;&lt;/tr&gt; 
